    public static int countGroups(List<String> related) {
        int count = related.size();
        for (int i = 0; i < related.size(); i++) {
            String line = related.get(i);
            for (int j = 0; j < i; j++) {
                if(line.charAt(j) == '1') {
                    count--;
                }
            }
        }
        return (count > 0) ? count : 1;
    }


